contracts/AgentWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AgentWallet
 * @dev EIP-1271 smart contract wallet for the Tail-Risk Protection Agent.
 *      Allows the AI agent to sign transactions on-chain, enabling:
 *      - Autonomous claim payments
 *      - Rebalancing approvals
 *      - Premium collection
 *
 *      Implements EIP-1271 isValidSignature() for smart contract signature validation.
 */
contract AgentWallet is EIP712, Ownable {
    using ECDSA for bytes32;

    // ─── EIP-1271 Magic Values ────────────────────────────────────────────────

    bytes4 private constant MAGIC_VALUE = 0x1626ba7e;
    bytes4 private constant INVALID_VALUE = 0xffffffff;

    // ─── Types ────────────────────────────────────────────────────────────────

    struct AgentAction {
        address target;
        uint256 value;
        bytes data;
        uint256 nonce;
        uint256 deadline;
    }

    bytes32 private constant AGENT_ACTION_TYPEHASH = keccak256(
        "AgentAction(address target,uint256 value,bytes data,uint256 nonce,uint256 deadline)"
    );

    // ─── State ────────────────────────────────────────────────────────────────

    uint256 public nonce;

    // Authorized signers (the AI agent's hot key)
    mapping(address => bool) public authorizedSigners;

    // Spending limits per target contract
    mapping(address => uint256) public dailyLimit;
    mapping(address => uint256) public dailySpent;
    mapping(address => uint256) public lastSpendDay;

    // ─── Events ───────────────────────────────────────────────────────────────

    event ActionExecuted(
        address indexed target,
        uint256 value,
        bytes data,
        uint256 nonce
    );

    event SignerAuthorized(address indexed signer, bool authorized);
    event DailyLimitSet(address indexed target, uint256 limit);

    // ─── Errors ───────────────────────────────────────────────────────────────

    error DeadlineExpired(uint256 deadline, uint256 current);
    error InvalidNonce(uint256 expected, uint256 provided);
    error DailyLimitExceeded(address target, uint256 limit, uint256 spent);
    error UnauthorizedSigner(address signer);
    error ExecutionFailed(bytes returnData);

    // ─── Constructor ──────────────────────────────────────────────────────────

    constructor(address agentSigner) EIP712("AgentWallet", "1") Ownable(msg.sender) {
        authorizedSigners[agentSigner] = true;
        emit SignerAuthorized(agentSigner, true);
    }

    // ─── EIP-1271 ─────────────────────────────────────────────────────────────

    /**
     * @notice Validate a signature against this wallet's authorized signers.
     * @dev Implements EIP-1271 for smart contract signature validation.
     */
    function isValidSignature(
        bytes32 hash,
        bytes memory signature
    ) external view returns (bytes4) {
        address signer = hash.recover(signature);
        if (authorizedSigners[signer]) {
            return MAGIC_VALUE;
        }
        return INVALID_VALUE;
    }

    // ─── Action Execution ─────────────────────────────────────────────────────

    /**
     * @notice Execute an agent action (signed by authorized signer).
     * @param action The action to execute.
     * @param signature EIP-712 signature from an authorized signer.
     */
    function execute(
        AgentAction calldata action,
        bytes calldata signature
    ) external returns (bytes memory returnData) {
        // Validate deadline
        if (block.timestamp > action.deadline) {
            revert DeadlineExpired(action.deadline, block.timestamp);
        }

        // Validate nonce
        if (action.nonce != nonce) {
            revert InvalidNonce(nonce, action.nonce);
        }

        // Validate signature
        bytes32 structHash = keccak256(abi.encode(
            AGENT_ACTION_TYPEHASH,
            action.target,
            action.value,
            keccak256(action.data),
            action.nonce,
            action.deadline
        ));
        bytes32 digest = _hashTypedDataV4(structHash);
        address signer = digest.recover(signature);

        if (!authorizedSigners[signer]) {
            revert UnauthorizedSigner(signer);
        }

        // Check daily spending limit
        if (action.value > 0 && dailyLimit[action.target] > 0) {
            uint256 today = block.timestamp / 1 days;
            if (lastSpendDay[action.target] < today) {
                dailySpent[action.target] = 0;
                lastSpendDay[action.target] = today;
            }
            if (dailySpent[action.target] + action.value > dailyLimit[action.target]) {
                revert DailyLimitExceeded(
                    action.target,
                    dailyLimit[action.target],
                    dailySpent[action.target]
                );
            }
            dailySpent[action.target] += action.value;
        }

        nonce++;

        // Execute the action
        bool success;
        (success, returnData) = action.target.call{value: action.value}(action.data);
        if (!success) revert ExecutionFailed(returnData);

        emit ActionExecuted(action.target, action.value, action.data, action.nonce);
    }

    // ─── Owner Management ─────────────────────────────────────────────────────

    function setSigner(address signer, bool authorized) external onlyOwner {
        authorizedSigners[signer] = authorized;
        emit SignerAuthorized(signer, authorized);
    }

    function setDailyLimit(address target, uint256 limit) external onlyOwner {
        dailyLimit[target] = limit;
        emit DailyLimitSet(target, limit);
    }

    // Allow receiving ETH
    receive() external payable {}
    fallback() external payable {}
}

contracts/IdentityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title IdentityRegistry
 * @dev ERC-8004 compliant Identity Registry for autonomous AI agents.
 *      Each agent receives an ERC-721 token (agentId) pointing to its
 *      Agent Registration File (agentURI) — a JSON document describing
 *      the agent's capabilities, endpoints, and trust model.
 *
 *      Implements ERC-8004 §Identity Registry specification.
 */
contract IdentityRegistry is ERC721URIStorage, Ownable {
    // ─── State ────────────────────────────────────────────────────────────────

    uint256 private _nextAgentId;

    // agentId → operator address (can update agentURI)
    mapping(uint256 => address) public operators;

    // agentId → metadataKey → metadataValue (on-chain metadata)
    mapping(uint256 => mapping(string => bytes)) private _metadata;

    // Reserved key per ERC-8004 spec — must NOT be set via setMetadata()
    string private constant AGENT_WALLET_KEY = "agentWallet";

    // agentId → agentWallet (payment address)
    mapping(uint256 => address) public agentWallets;

    // ─── Events ───────────────────────────────────────────────────────────────

    event AgentRegistered(
        uint256 indexed agentId,
        address indexed owner,
        string agentURI
    );

    event AgentURIUpdated(uint256 indexed agentId, string newURI);

    event OperatorSet(
        uint256 indexed agentId,
        address indexed operator
    );

    event MetadataSet(
        uint256 indexed agentId,
        string indexed indexedMetadataKey,
        string metadataKey,
        bytes metadataValue
    );

    event AgentWalletUpdated(
        uint256 indexed agentId,
        address indexed newWallet,
        bytes proof
    );

    // ─── Errors ───────────────────────────────────────────────────────────────

    error NotAuthorized(uint256 agentId, address caller);
    error ReservedKey(string key);
    error AgentNotExists(uint256 agentId);

    // ─── Constructor ──────────────────────────────────────────────────────────

    constructor() ERC721("ERC-8004 Agent Identity", "AGENT") Ownable(msg.sender) {
        _nextAgentId = 1; // start at 1, 0 is reserved
    }

    // ─── Registration ─────────────────────────────────────────────────────────

    /**
     * @notice Register a new agent and mint an ERC-721 identity token.
     * @param agentURI URI pointing to the Agent Registration File (JSON).
     * @return agentId The newly minted token ID.
     */
    function register(string calldata agentURI) external returns (uint256 agentId) {
        agentId = _nextAgentId++;
        _safeMint(msg.sender, agentId);
        _setTokenURI(agentId, agentURI);
        agentWallets[agentId] = msg.sender; // default wallet = owner
        emit AgentRegistered(agentId, msg.sender, agentURI);
    }

    /**
     * @notice Register with additional on-chain metadata.
     */
    function register(
        string calldata agentURI,
        string[] calldata keys,
        bytes[] calldata values
    ) external returns (uint256 agentId) {
        require(keys.length == values.length, "Keys/values length mismatch");
        agentId = _nextAgentId++;
        _safeMint(msg.sender, agentId);
        _setTokenURI(agentId, agentURI);
        agentWallets[agentId] = msg.sender;

        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keccak256(bytes(AGENT_WALLET_KEY))) {
                revert ReservedKey(AGENT_WALLET_KEY);
            }
            _metadata[agentId][keys[i]] = values[i];
            emit MetadataSet(agentId, keys[i], keys[i], values[i]);
        }

        emit AgentRegistered(agentId, msg.sender, agentURI);
    }

    // ─── URI Management ───────────────────────────────────────────────────────

    /**
     * @notice Update the agent's registration file URI.
     * @dev Only callable by token owner or authorized operator.
     */
    function setAgentURI(uint256 agentId, string calldata newURI) external {
        _requireAuthorized(agentId);
        _setTokenURI(agentId, newURI);
        emit AgentURIUpdated(agentId, newURI);
    }

    // ─── Operator Management ──────────────────────────────────────────────────

    /**
     * @notice Delegate management rights (e.g., URI updates) to an operator.
     */
    function setOperator(uint256 agentId, address operator) external {
        if (ownerOf(agentId) != msg.sender) revert NotAuthorized(agentId, msg.sender);
        operators[agentId] = operator;
        emit OperatorSet(agentId, operator);
    }

    // ─── Metadata ─────────────────────────────────────────────────────────────

    /**
     * @notice Get on-chain metadata for an agent.
     */
    function getMetadata(
        uint256 agentId,
        string memory metadataKey
    ) external view returns (bytes memory) {
        if (!_exists(agentId)) revert AgentNotExists(agentId);
        return _metadata[agentId][metadataKey];
    }

    /**
     * @notice Set on-chain metadata for an agent.
     * @dev The key "agentWallet" is reserved — use updateAgentWallet() instead.
     */
    function setMetadata(
        uint256 agentId,
        string memory metadataKey,
        bytes memory metadataValue
    ) external {
        _requireAuthorized(agentId);
        if (keccak256(bytes(metadataKey)) == keccak256(bytes(AGENT_WALLET_KEY))) {
            revert ReservedKey(AGENT_WALLET_KEY);
        }
        _metadata[agentId][metadataKey] = metadataValue;
        emit MetadataSet(agentId, metadataKey, metadataKey, metadataValue);
    }

    // ─── Agent Wallet ─────────────────────────────────────────────────────────

    /**
     * @notice Update the agent's payment wallet address.
     * @dev Requires a proof of control (signed message from new wallet).
     *      In production, proof should be validated on-chain. For hackathon
     *      purposes, we accept any non-zero proof bytes.
     */
    function updateAgentWallet(
        uint256 agentId,
        address newWallet,
        bytes calldata proof
    ) external {
        _requireAuthorized(agentId);
        require(newWallet != address(0), "Invalid wallet address");
        require(proof.length > 0, "Proof required");
        agentWallets[agentId] = newWallet;
        emit AgentWalletUpdated(agentId, newWallet, proof);
    }

    // ─── View Helpers ─────────────────────────────────────────────────────────

    function totalAgents() external view returns (uint256) {
        return _nextAgentId - 1;
    }

    function agentURI(uint256 agentId) external view returns (string memory) {
        return tokenURI(agentId);
    }

    // ─── Internal ─────────────────────────────────────────────────────────────

    function _requireAuthorized(uint256 agentId) internal view {
        address owner = ownerOf(agentId);
        if (msg.sender != owner && msg.sender != operators[agentId]) {
            revert NotAuthorized(agentId, msg.sender);
        }
    }

    function _exists(uint256 agentId) internal view returns (bool) {
        return agentId > 0 && agentId < _nextAgentId;
    }
}

contracts/ReputationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title ReputationRegistry
 * @dev ERC-8004 compliant Reputation Registry for AI agents.
 *      Allows clients and validators to post feedback signals.
 *      Aggregates scores on-chain for composability.
 *
 *      For the Tail-Risk Agent, reputation signals include:
 *      - Claim payout accuracy (did the agent pay valid claims?)
 *      - Rebalancing effectiveness (did rebalances protect capital?)
 *      - Premium pricing fairness (were premiums actuarially sound?)
 */
contract ReputationRegistry {
    // ─── Types ────────────────────────────────────────────────────────────────

    enum FeedbackType {
        CLAIM_PAYOUT,       // Agent paid a legitimate claim
        REBALANCE,          // Agent executed a protective rebalance
        PREMIUM_PRICING,    // Agent priced a premium
        GENERAL             // General performance feedback
    }

    struct Feedback {
        address reviewer;
        uint256 agentId;
        uint8 score;         // 0-100
        FeedbackType feedbackType;
        string comment;
        uint256 timestamp;
        bytes32 evidenceHash; // hash of off-chain evidence
    }

    struct ReputationSummary {
        uint256 totalFeedback;
        uint256 totalScore;     // sum of all scores
        uint256 claimPayouts;   // # of claim payout feedbacks
        uint256 rebalances;     // # of rebalance feedbacks
        uint256 premiumPricings;
        uint8 latestScore;      // most recent score
    }

    // ─── State ────────────────────────────────────────────────────────────────

    // agentId → list of feedbacks
    mapping(uint256 => Feedback[]) private _feedbacks;

    // agentId → aggregated reputation summary
    mapping(uint256 => ReputationSummary) public reputations;

    // reviewer → agentId → has reviewed (prevent spam)
    mapping(address => mapping(uint256 => uint256)) public lastReviewTimestamp;

    // Minimum time between reviews from same address (1 hour)
    uint256 public constant REVIEW_COOLDOWN = 1 hours;

    // ─── Events ───────────────────────────────────────────────────────────────

    event FeedbackPosted(
        uint256 indexed agentId,
        address indexed reviewer,
        uint8 score,
        FeedbackType feedbackType,
        bytes32 evidenceHash
    );

    event ReputationUpdated(
        uint256 indexed agentId,
        uint256 newAverageScore,
        uint256 totalFeedback
    );

    // ─── Errors ───────────────────────────────────────────────────────────────

    error InvalidScore(uint8 score);
    error ReviewCooldownActive(address reviewer, uint256 cooldownEnds);
    error InvalidAgentId();

    // ─── Core Functions ───────────────────────────────────────────────────────

    /**
     * @notice Post feedback for an agent.
     * @param agentId The ERC-8004 agent token ID.
     * @param score Score from 0-100.
     * @param feedbackType Category of feedback.
     * @param comment Human-readable description.
     * @param evidenceHash Keccak256 hash of off-chain evidence (IPFS CID, tx hash, etc.)
     */
    function postFeedback(
        uint256 agentId,
        uint8 score,
        FeedbackType feedbackType,
        string calldata comment,
        bytes32 evidenceHash
    ) external {
        if (agentId == 0) revert InvalidAgentId();
        if (score > 100) revert InvalidScore(score);

        uint256 cooldownEnd = lastReviewTimestamp[msg.sender][agentId] + REVIEW_COOLDOWN;
        if (block.timestamp < cooldownEnd) {
            revert ReviewCooldownActive(msg.sender, cooldownEnd);
        }

        _feedbacks[agentId].push(Feedback({
            reviewer: msg.sender,
            agentId: agentId,
            score: score,
            feedbackType: feedbackType,
            comment: comment,
            timestamp: block.timestamp,
            evidenceHash: evidenceHash
        }));

        lastReviewTimestamp[msg.sender][agentId] = block.timestamp;

        // Update aggregated reputation
        ReputationSummary storage rep = reputations[agentId];
        rep.totalFeedback++;
        rep.totalScore += score;
        rep.latestScore = score;

        if (feedbackType == FeedbackType.CLAIM_PAYOUT) rep.claimPayouts++;
        else if (feedbackType == FeedbackType.REBALANCE) rep.rebalances++;
        else if (feedbackType == FeedbackType.PREMIUM_PRICING) rep.premiumPricings++;

        uint256 avgScore = rep.totalScore / rep.totalFeedback;

        emit FeedbackPosted(agentId, msg.sender, score, feedbackType, evidenceHash);
        emit ReputationUpdated(agentId, avgScore, rep.totalFeedback);
    }

    // ─── View Functions ───────────────────────────────────────────────────────

    /**
     * @notice Get the average reputation score for an agent (0-100).
     */
    function getAverageScore(uint256 agentId) external view returns (uint256) {
        ReputationSummary storage rep = reputations[agentId];
        if (rep.totalFeedback == 0) return 0;
        return rep.totalScore / rep.totalFeedback;
    }

    /**
     * @notice Fetch all feedbacks for an agent (paginated).
     */
    function getFeedbacks(
        uint256 agentId,
        uint256 offset,
        uint256 limit
    ) external view returns (Feedback[] memory) {
        Feedback[] storage all = _feedbacks[agentId];
        uint256 end = offset + limit;
        if (end > all.length) end = all.length;
        uint256 size = end > offset ? end - offset : 0;

        Feedback[] memory result = new Feedback[](size);
        for (uint256 i = 0; i < size; i++) {
            result[i] = all[offset + i];
        }
        return result;
    }

    /**
     * @notice Get total number of feedbacks for an agent.
     */
    function getFeedbackCount(uint256 agentId) external view returns (uint256) {
        return _feedbacks[agentId].length;
    }

    /**
     * @notice Get a Sharpe-like trust score combining quantity and quality.
     *         score = avg_score × log(1 + total_feedback) / 10
     *         Returns 0-100 normalized.
     */
    function getTrustScore(uint256 agentId) external view returns (uint256) {
        ReputationSummary storage rep = reputations[agentId];
        if (rep.totalFeedback == 0) return 0;

        uint256 avg = rep.totalScore / rep.totalFeedback;
        // Approximate log with integer arithmetic: log2(n) ≈ bit_length - 1
        uint256 logFactor = _log2(rep.totalFeedback + 1);
        uint256 raw = avg * logFactor;

        // Normalize: max avg=100, max logFactor≈17 (for 2^17=131072 feedbacks)
        // raw_max = 1700, normalize to 100
        return raw > 1700 ? 100 : (raw * 100) / 1700;
    }

    // ─── Internal ─────────────────────────────────────────────────────────────

    function _log2(uint256 x) internal pure returns (uint256) {
        uint256 result = 0;
        while (x > 1) {
            x >>= 1;
            result++;
        }
        return result;
    }
}

